import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Createnote from "./Createnote";
import Note from "./Note"


function App() {
  const [addItem, setitem] = useState();
  const addnote = (note) => {
    setitem((oldItem) => {
      return [...oldItem, ...note]

    })

  }

  return (
    <>
      <Header />
      <Createnote passnote={addnote} />
      <Note />
      <Footer />


    </>
  )
}

export default App;
